function qwerty(n) {
    var res = [1];
    for (var i = 2; i <= n; ++i) {
        var c = 0;
        for (var j = 0; j < res.length || c !== 0; ++j) {
            c += (res[j] || 0) * i;
            res[j] = c % 10;
            c = Math.floor(c / 10);
        }
    }
    return res.reverse().join("");
}

describe('factorial', function() {
    it('should handle small numbers', function() {
        Test.assertEquals(factorial(1), '1', '1!');
        Test.assertEquals(factorial(5), '120', '5!');
        Test.assertEquals(factorial(9), '362880', '9!');
        Test.assertEquals(factorial(15), '1307674368000', '15!');
    });

    it('Random Small Tests', function() {
        var a, b, i;
        for(i = 0; i < 10; i++) {
        a =  Math.round(Math.random() * 10);
        Test.assertEquals(factorial(a), qwerty(a), a+'!');
        }
    });

    it('should handle big numbers', function() {
        var sum;
        
        sum = factorial(100);
        Test.expect(sum === '93326215443944152681699238856266700490715968264381621468592963895217599993229915608941463976156518286253697920827223758251185210916864000000000000000000000000');
        
        sum = factorial(150);
        Test.expect(sum === '57133839564458545904789328652610540031895535786011264182548375833179829124845398393126574488675311145377107878746854204162666250198684504466355949195922066574942592095735778929325357290444962472405416790722118445437122269675520000000000000000000000000000000000000');

        sum = factorial(200);
        Test.expect(sum === '');
        
        sum = factorial(250);
        Test.expect(sum === '');
        });

    it('Random Large Tests', function() {
        var a;
        for(i = 0; i < 10; i++) {
        a =  Math.round(Math.random() * 80);
        a += 100;
        Test.assertEquals(factorial(a), qwerty(a), a+'!');
        }
    }); 
});
